import { useState } from "react";
import  "../Form.css";

const Form=(props)=>{
    const {setMax,setMin,setCounter,max,min}=props;


    const[formCounter,setFormCounter]=useState(null);
    const [formMax,setFormMax]=useState(null);
    const [formMin,setFormMin]=useState(null);

    const handleCounterChange=(e)=>{
        setFormCounter(e.target.value);
    }
    const handleMaxChange=(e)=>{
        setFormMax(e.target.value);
    }

    const handleMinChage=(e)=>{
        setFormMin(e.target.value);
    }


    const onSubmitHandler=(e)=>{
        e.preventDefault();
        console.log(formCounter,formMax,formMin)

        if(formMax!==null) setMax(parseInt(formMax));
        if(formMin!==null) setMin(parseInt(formMin)); 

        if(formCounter!==null) {
            if((formMax==null && parseInt(formCounter)>max)) {
                alert("Counter is greater than upper limit.");
                return;
            }else if(formMin===null && parseInt(formCounter)<min){
                alert("Counter is less than lower limit");
                return;
            }
            else if(parseInt(formCounter)>parseInt(formMax)){
                alert("Counter is greater than upper limit.");
                return;
            }
            else if(parseInt(formCounter)<parseInt(formMin)){
                alert("Counter is less than lower limit");
                return;
            }
            setCounter(parseInt(formCounter));
        }

        // console.log(counter,max,min);
    }

    return(
        <div className="FormCont">
            <form className="FormInput" onSubmit={onSubmitHandler}>
                <label className="Input" value={formCounter} onChange={handleCounterChange}>
                    Set Counter Value:
                    <input />
                </label>
                <label className="Input" value={formMax} onChange={handleMaxChange}>
                    Set Max Value:
                     <input/>
                </label>
                <label className="Input" value={formMin}  onChange={handleMinChage}>
                     Set Min Value:
                     <input/>
                </label>
                <button className="button">Submit</button>
            </form>
        </div>
    )
}

export default Form;
