import {useState,useEffect} from 'react';
import Loader from '../Components/Loader';

const HomeScreen=()=>{
    const [counter,setCounter]=useState(1);
    const [loading,setisLoading]=useState(true);
    const maxvalue=1000;

    async function fetchData(){     
      let data;
      const response=await fetch('https://interview-8e4c5-default-rtdb.firebaseio.com/front-end/counter1.json');
      if(response.ok){
       data=await response.json();
      }else{
        console.log("some error in getting response");
      }

      if(data==null){
        setCounter(1);
      }else{
        setCounter(data);
      }
      setisLoading(false);
      }

    async function putData(counterData){

      const response = await fetch('https://interview-8e4c5-default-rtdb.firebaseio.com/front-end.json', {
        method: 'PUT',
        body:JSON.stringify(counterData)
      });

    }

    useEffect(()=>{
        fetchData();

    },[])

    const handleDecrement=()=>{
       const num=counter-1;
       const counterData={nirupamaRanjan:num};
       putData(counterData);
       setCounter(num);


    }

    const handleIncrement=()=>{
       const num=counter+1;
       if(num>maxvalue){
         alert("you have reached the limit");
         return;
       }
       const counterData={nirupamaRanjan:num};
       putData(counterData);
       setCounter(num);

    }

    return (

           <div className="App">

             <div className="container">
                { loading ?  <div className="loader"><span><Loader/></span>Saving counter value</div> : <div className="loader"></div>}
               <div className="buttonContainer">
               <button  className="buttonL" title="+" onClick={handleDecrement}>
                    -
               </button>
               <div className="counter">{counter}</div>
               <button title="+" className="buttonR" onClick={handleIncrement}>
                       +
               </button>
               </div>
               <div className="value" >Counter Value: {counter}</div>
             </div>
           </div>

    );
}

export default HomeScreen
