import { useState } from "react";
import  "../Form.css";

const Form=(props)=>{

    const[formCounter,setFormCounter]=useState(null);
    const [formMax,setFormMax]=useState(null);
    const [formMin,setFormMin]=useState(null);

    const handleCounterChange=(e)=>{
        setFormCounter(e.target.value);
    }
    const handleMaxChange=(e)=>{
        setFormMax(e.target.value);
    }

    const handleMinChage=(e)=>{
        setFormMin(e.target.value);
    }

    const {setMax,setMin,setCounter}=props;

    const onSubmitHandler=(e)=>{
        e.preventDefault();
        setFormCounter(null);
        setFormMax(null);
        setFormMin(null);
        if(formCounter!==null) setCounter(formCounter);
        // setMax(max);
        // setMin(min); 


        console.log(counter,max,min);
    }

    return(
        <div className="FormCont">
            <form className="FormInput" onSubmit={onSubmitHandler}>
                <label className="Input" value={formCounter} onChange={handleCounterChange}>
                    Set Counter Value:
                    <input />
                </label>
                <label className="Input" value={formMax} onChange={handleMaxChange}>
                    Set Max Value:
                     <input/>
                </label>
                <label className="Input" value={formMin}  onChange={handleMinChage}>
                     Set Min Value:
                     <input/>
                </label>
                <button className="button">Submit</button>
            </form>
        </div>
    )
}

export default Form;
