import './App.css';
import {useState,useEffect} from 'react';


function App() {

  const [counter,setCounter]=useState(1);
  const [loading,isLoading]=useState(true);
  const maxvalue=1000;

  useEffect(()=>{
    fetch('https://interview-8e4c5-default-rtdb.firebaseio.com/front-end/counter1.json')
      .then(response=>  {  response.json() ; console.log(response) } )
      .then(response =>{
        let counter=0;
        if(response===null){
           counter=1;
        }else{
          counter=response;
        }
        setCounter(counter);
      })

  },[])

  const handleDecrement=()=>{
     const num=counter-1;
     setCounter(num);

  }

  const handleIncrement=()=>{
     const num=counter+1;
     if(num>maxvalue){
       alert("you have reached the limit");
       return;
     }
     setCounter(num);
  }

  return (

         <div className="App">

           <div className="container">
             {loading && <div className="loader">saving counter value</div>}
             <div className="buttonContainer">
             <button  className="buttonL" title="+" onClick={handleDecrement}>
                  -
             </button>
             <div className="counter">{counter}</div>
             <button title="+" className="buttonR" onClick={handleIncrement}>
                     +
             </button>
             </div>
             <div className="value" >Counter Value: {counter}</div>
           </div>
         </div>

  );
}

export default App;
