import { useState } from "react";
import  "../Form.css";

const Form=(props)=>{
    const {setMax,setMin,setCounter,max,min}=props;


    const[formCounter,setFormCounter]=useState("");
    const [formMax,setFormMax]=useState();
    const [formMin,setFormMin]=useState("");

    const handleCounterChange=(e)=>{
        setFormCounter(e.target.value);
    }
    const handleMaxChange=(e)=>{
        setFormMax(e.target.value);
    }

    const handleMinChage=(e)=>{
        setFormMin(e.target.value);
    }


    const onSubmitHandler=(e)=>{
        e.preventDefault();

        if(formMax!=="") setMax(parseInt(formMax)) 
        else setMax(max);
        if(formMin!=="") setMin(parseInt(formMin))
        else setMin(min); 

        if(formCounter!=="") {
            if((formMax==="" && parseInt(formCounter)>max)) {
                alert("Counter is greater than upper limit.");
                setFormCounter("");
                return;
            }else if(formMin==="" && parseInt(formCounter)<min){
                alert("Counter is less than lower limit");
                setFormCounter("");
                return;
            }
            else if(parseInt(formCounter)>parseInt(formMax)){
                alert("Counter is greater than upper limit.");
                setFormCounter("");
                return;
            }
            else if(parseInt(formCounter)<parseInt(formMin)){
                alert("Counter is less than lower limit");
                setFormCounter("");
                return;
            }
            setCounter(parseInt(formCounter));
        }

        setFormCounter("");
        setFormMax("");
        setFormMin("");

        console.log("f",formCounter,"mAx",formMax,formMin)   
    }

    return(
        <div className="FormCont">
            <form className="FormInput" onSubmit={onSubmitHandler}>
                <label className="Input">
                    Set Counter Value:
                    <input value={formCounter}  onChange={handleCounterChange}/>
                </label>
                <label className="Input" >
                    Set Max Value:
                     <input value={formMax} onChange={handleMaxChange}/>
                </label>
                <label className="Input" >
                     Set Min Value:
                     <input value={formMin}  onChange={handleMinChage}/>
                </label>
                <button className="button">Submit</button>
            </form>
        </div>
    )
}

export default Form;
