import { useState } from "react";
import  "../Form.css";

const Form=(props)=>{

    const[counter,setFormCounter]=useState(null);
    const [max,setFormMax]=useState(1000);
    const [min,setFormMin]=useState(-1000);

    const handleCounterChange=(e)=>{
        setFormCounter(e.target.value);
    }
    const handleMaxChange=(e)=>{
        setFormMax(e.target.value);
    }

    const handleMinChage=(e)=>{
        setFormMin(e.target.value);
    }

    const {setMax,setMin,setCounter}=props;

    const onSubmitHandler=()=>{
        if(counter!==null) setCounter(counter);
        setMax(max);
        setMin(min);    
    }

    return(
        <div className="FormCont">
            <form className="FormInput" onSubmit={onSubmitHandler}>
                <label className="Input" value={counter} onChange={handleCounterChange}>
                    Set Counter Value:
                    <input />
                </label>
                <label className="Input" value={max} onChange={handleMaxChange}>
                    Set Max Value:
                     <input/>
                </label>
                <label className="Input" value={min}  onChange={handleMinChage}>
                     Set Min Value:
                     <input/>
                </label>
                <button className="button">Submit</button>
            </form>
        </div>
    )
}

export default Form;
