import { useState } from "react";
import  "../Form.css";

const Form=(props)=>{
    const {setMax,setMin,setCounter,max,min}=props;

    const[formCounter,setFormCounter]=useState(NaN);
    const [formMax,setFormMax]=useState(1000);
    const [formMin,setFormMin]=useState(-1000);

    const onSubmitHandler=(e)=>{
        e.preventDefault();

        if(formMax!=="")  setMax(formMax) 
        if(formMin!=="") setMin(formMin)

        if(formCounter!=="") {
            if(formCounter>max){
                alert("Counter is greater than upper limit");
                setFormCounter("");
                return;
            }
            if(formCounter<min){
                alert("Counter is smaller than lower limit");
                setFormCounter("");
                return;
            }
          setCounter(formCounter);      
        }

        setFormCounter("");
        setFormMax("");
        setFormMin("");
    }

    return(
        <div className="FormCont">
            <form className="FormInput" onSubmit={onSubmitHandler}>
                <label>
                    Set Counter Value:
                    <input type="number" value={formCounter}  onChange={e=>setFormCounter(e.target.value)}/>
                </label>
                <label >
                    Set Max Value:
                     <input type="number" value={formMax} onChange={e=>setFormMax(e.target.value)}/>
                </label>
                <label className="Input" >
                     Set Min Value:
                     <input type="number" value={formMin}  onChange={e=>setFormMin(e.target.value)}/>
                </label>
                <button className="button">Submit</button>
            </form>
        </div>
    )
}

export default Form;
